nta.declaration:
// Place global declarations here.
clock globalTime;
const int INFINITE_TIME = -1;
const int N_REACTANTS = 12;
broadcast chan reacting[N_REACTANTS];

//R8 = RAF
int R8 := 4;

//R9 = RKIP
int R9 := 9;

//R6 = NGF
int R6 := 2;

//R7 = PKC
int R7 := 12;

//R4 = ERK
int R4 := 67;

//R5 = MEK
int R5 := 10;

//R2 = (3)
int R2 := 1;

//R3 = EGF
int R3 := 0;

//R1 = (2)
int R1 := 1;

//R0 = (1)
int R0 := 1;

//R11 = neutralizing antibody
int R11 := 1;

//R10 = introduction of Ab
int R10 := 1;

typedef struct {
	int[-99980001, 99980001] b;
	int e;
} double_t;

typedef int[-1, 1073741822] time_t;

const double_t zero = {0, 0};
const double_t INFINITE_TIME_DOUBLE = {-1000, -3}; //INFINITE_TIME translated into double_t
const double_t timeScale = {1200, -1}; //The timeScale factor
const double_t uncertaintyUpper = {1000, -3}, //0 uncertainty
			 uncertaintyLower = {1000, -3};

double_t subtract(double_t a, double_t b) { // a - b
	double_t r = {-1000, -1000};
	if (a.b == 0) {
		r.b = -b.b;
		r.e = b.e;
		return r;
	}
	if (b.b == 0) {
		return a;
	}
	if ((a.e - b.e) >= 4) return a;
	if ((b.e - a.e) >= 4) {
		r.b = -b.b;
		r.e = b.e;
		return r;
	}
	if (a.e == b.e) {
		r.b = a.b - b.b;
		r.e = a.e;
	}
	if (a.e - b.e == 1) {
		r.b = a.b - b.b/10;
		r.e = a.e;
	}
	if (a.e - b.e == 2) {
		r.b = a.b - b.b/100;
		r.e = a.e;
	}
	if (a.e - b.e == 3) {
		r.b = a.b - b.b/1000;
		r.e = a.e;
	}
	if (b.e - a.e == 1) {
		r.b = a.b/10 - b.b;
		r.e = b.e;
	}
	if (b.e - a.e == 2) {
		r.b = a.b/100 - b.b;
		r.e = b.e;
	}
	if (b.e - a.e == 3) {
		r.b = a.b/1000 - b.b;
		r.e = b.e;
	}
	if ((r.b > 0 && r.b < 10) || (r.b < 0 && r.b > -10)) {
		r.b = r.b * 1000;
		r.e = r.e - 3;
	} else if ((r.b > 0 && r.b < 100) || (r.b < 0 && r.b > -100)) {
		r.b = r.b * 100;
		r.e = r.e - 2;
	} else if ((r.b > 0 && r.b < 1000) || (r.b < 0 && r.b > -1000)) {
		r.b = r.b * 10;
		r.e = r.e - 1;
	} else if (r.b > 9999 || r.b < -9999) {
		r.b = r.b / 10;
		r.e = r.e + 1;
	}
	return r;
}

double_t add(double_t a, double_t b) { // a + b
	double_t r = {-1000,-1000};
	if (a.b == 0) {
		return b;
	}
	if (b.b == 0) {
		return a;
	}
	if ((a.e - b.e) >= 4) return a;
	if ((b.e - a.e) >= 4) return b;
	if (a.e == b.e) {
		r.b = a.b + b.b;
		r.e = a.e;
	}
	if (a.e - b.e == 1) {
		r.b = a.b + b.b/10;
		r.e = a.e;
	}
	if (a.e - b.e == 2) {
		r.b = a.b + b.b/100;
		r.e = a.e;
	}
	if (a.e - b.e == 3) {
		r.b = a.b + b.b/1000;
		r.e = a.e;
	}
	if (b.e - a.e == 1) {
		r.b = a.b/10 + b.b;
		r.e = b.e;
	}
	if (b.e - a.e == 2) {
		r.b = a.b/100 + b.b;
		r.e = b.e;
	}
	if (b.e - a.e == 3) {
		r.b = a.b/1000 + b.b;
		r.e = b.e;
	}
	if ((r.b > 0 && r.b < 10) || (r.b < 0 && r.b > -10)) {
		r.b = r.b * 1000;
		r.e = r.e - 3;
	} else if ((r.b > 0 && r.b < 100) || (r.b < 0 && r.b > -100)) {
		r.b = r.b * 100;
		r.e = r.e - 2;
	} else if ((r.b > 0 && r.b < 1000) || (r.b < 0 && r.b > -1000)) {
		r.b = r.b * 10;
		r.e = r.e - 1;
	} else if (r.b > 9999 || r.b < -9999) {
		r.b = r.b / 10;
		r.e = r.e + 1;
	}
	return r;
}

double_t multiply(double_t a, double_t b) { // a * b
	double_t r;
	r.b = a.b * b.b;
	if (r.b % 1000 < 500) {
		r.b = r.b / 1000;
	} else {
		r.b = 1 + r.b / 1000;
	}
	r.e = a.e + b.e + 3;
	if ((r.b > 0 && r.b < 10) || (r.b < 0 && r.b > -10)) {
		r.b = r.b * 1000;
		r.e = r.e - 3;
	} else if ((r.b > 0 && r.b < 100) || (r.b < 0 && r.b > -100)) {
		r.b = r.b * 100;
		r.e = r.e - 2;
	} else if ((r.b > 0 && r.b < 1000) || (r.b < 0 && r.b > -1000)) {
		r.b = r.b * 10;
		r.e = r.e - 1;
	} else if (r.b > 9999 || r.b < -9999) {
		r.b = r.b / 10;
		r.e = r.e + 1;
	}
	return r;
}

double_t inverse(double_t a) { // 1 / a
	double_t r;
	if (a.b == 0) {
		return INFINITE_TIME_DOUBLE;
	}
	r.b = 1000000 / a.b;
	r.e = -6 - a.e;
	if ((r.b > 0 && r.b < 10) || (r.b < 0 && r.b > -10)) {
		r.b = r.b * 1000;
		r.e = r.e - 3;
	} else if ((r.b > 0 && r.b < 100) || (r.b < 0 && r.b > -100)) {
		r.b = r.b * 100;
		r.e = r.e - 2;
	} else if ((r.b > 0 && r.b < 1000) || (r.b < 0 && r.b > -1000)) {
		r.b = r.b * 10;
		r.e = r.e - 1;
	}
	return r;
}

time_t power(int a, int b) { // a ^ b (b >= 0)
	time_t r = 1;
	while (b > 0) {
		r = r * a;
		b = b - 1;
	}
	return r;
}

time_t round(double_t a) { // double --> integer
	if (a.e < -3) {
		if (a.b < 5000) return 0;
		else return 1;
	}
	if (a.e == -1) {
		if (a.b % 10 < 5) {
			return a.b / 10;
		} else {
			return 1 + a.b / 10;
		}
	}
	if (a.e == -2) {
		if (a.b % 100 < 50) {
			return a.b / 100;
		} else {
			return 1 + a.b / 100;
		}
	}
	if (a.e == -3) {
		if (a.b % 1000 < 500) {
			return a.b / 1000;
		} else {
			return 1 + a.b / 1000;
		}
	}
	return a.b * power(10, a.e);
}

double_t scenario1(double_t k, double_t r1, double_t r1Levels, bool r1Active) {
	double_t E;
	if (r1Active) { //If we depend on active R1, the level of activity is the value of E
		E = r1;
	} else { //otherwise we find the inactivity level via the total number of levels
		E = subtract(r1Levels, r1);
	}
	return multiply(k, E);
}


double_t scenario2_3(double_t k, double_t r2, double_t r2Levels, bool r2Active, double_t r1, double_t r1Levels, bool r1Active) {
	double_t E, S;
	if (r1Active) { //If we depend on active R1, the level of activity is the value of E
		E = r1;
	} else { //otherwise we find the inactivity level via the total number of levels
		E = subtract(r1Levels, r1);
	}
	if (r2Active) { //Same for R2
		S = r2;
	} else {
		S = subtract(r2Levels, r2);
	}
	return multiply(k, multiply(E, S));
}


double_t int_to_double(int a) { //Used to translate an activity level into double.
	double_t r;
	if (a < 10) {
		r.b = a * 1000;
		r.e = -3;
	} else if (a < 100) {
		r.b = a * 100;
		r.e = -2;
	} else if (a < 1000) {
		r.b = a * 10;
		r.e = -1;
	} else if (a < 10000) { //Our model supports up to 100 levels, so this should be the most we can check
		r.b = a;
		r.e = 0;
	}
	return r;
}

template.declaration:
clock c;
time_t T, tL, tU;
const double_t k = {6500, -4};
const double_t levelsScale = {6000, -2};
const double_t scale = multiply(levelsScale, timeScale);
const double_t R1Levels = {6000, -2},
			 R2Levels = {6000, -2};
double_t r;

void update() {
	r = scenario2_3(k, int_to_double(reactant2), R2Levels, false, int_to_double(reactant1), R1Levels, true);
	if (r.b != 0) {
		double_t time = multiply(inverse(r), scale);
		tL = round(multiply(time, uncertaintyLower));
		tU = round(multiply(time, uncertaintyUpper));
	} else {
		tL = INFINITE_TIME;
		tU = INFINITE_TIME;
	}
	if (tL != INFINITE_TIME && tU != INFINITE_TIME && tL > tU) { //We use rounded things: maybe the difference between tL and tU was not so great, and with some rounding problems we could have this case
		tL = tU;
	}
}

void react() {
	output = output + delta;
	update();
}

