		tU = round(multiply(time, uncertaintyUpper));
	} else {
		tL = INFINITE_TIME;
		tU = INFINITE_TIME;
	}
	if (tL != INFINITE_TIME && tU != INFINITE_TIME && tL > tU) { //We use rounded things: maybe the difference between tL and tU was not so great, and with some rounding problems we could have this case
		tL = tU;
	}
}

void react() {
	output = output + delta;
	update();
}

clock c;
time_t T, tL, tU;
const double_t k = {2500, -4};
const double_t levelsScale = {2000, -2};
const double_t scale = multiply(levelsScale, timeScale);
const double_t R1Levels = {2000, -2},
			 R2Levels = {6000, -2};
double_t r;

void update() {
	r = scenario2_3(k, int_to_double(reactant2), R2Levels, true, int_to_double(reactant1), R1Levels, true);
	if (r.b != 0) {
		double_t time = multiply(inverse(r), scale);
		tL = round(multiply(time, uncertaintyLower));
		tU = round(multiply(time, uncertaintyUpper));
	} else {
		tL = INFINITE_TIME;
		tU = INFINITE_TIME;
	}
	if (tL != INFINITE_TIME && tU != INFINITE_TIME && tL > tU) { //We use rounded things: maybe the difference between tL and tU was not so great, and with some rounding problems we could have this case
		tL = tU;
	}
}

void react() {
	output = output + delta;
	update();
}

