//Reaction R10 ((1)) --| R2 (RAF)
timeActivity R10_R2;
const double k_R10_R2 = {3330, -7};



double subtract(double a, double b) { // a - b
	double r = {-1000, -1000};
	if (a.b == 0) {
		r.b = -b.b;
		r.e = b.e;
		return r;
	}
	if (b.b == 0) {
		return a;
	}
	if ((a.e - b.e) >= 4) return a;
	if ((b.e - a.e) >= 4) {
		r.b = -b.b;
		r.e = b.e;
		return r;
	}
	if (a.e == b.e) {
		r.b = a.b - b.b;
		r.e = a.e;
	}
	if (a.e - b.e == 1) {
		r.b = a.b - b.b/10;
		r.e = a.e;
	}
	if (a.e - b.e == 2) {
		r.b = a.b - b.b/100;
		r.e = a.e;
	}
	if (a.e - b.e == 3) {
		r.b = a.b - b.b/1000;
		r.e = a.e;
	}
	if (b.e - a.e == 1) {
		r.b = a.b/10 - b.b;
		r.e = b.e;
	}
	if (b.e - a.e == 2) {
		r.b = a.b/100 - b.b;
		r.e = b.e;
	}
	if (b.e - a.e == 3) {
		r.b = a.b/1000 - b.b;
		r.e = b.e;
	}
	if ((r.b > 0 && r.b < 10) || (r.b < 0 && r.b > -10)) {
		r.b = r.b * 1000;
		r.e = r.e - 3;
	} else if ((r.b > 0 && r.b < 100) || (r.b < 0 && r.b > -100)) {
		r.b = r.b * 100;
		r.e = r.e - 2;
	} else if ((r.b > 0 && r.b < 1000) || (r.b < 0 && r.b > -1000)) {
		r.b = r.b * 10;
		r.e = r.e - 1;
	}
	return r;
}

double add(double a, double b) { // a + b
	double r = {-1000,-1000};
	if (a.b == 0) {
		return b;
	}
	if (b.b == 0) {
		return a;
	}
	if ((a.e - b.e) >= 4) return a;
	if ((b.e - a.e) >= 4) return b;
	if (a.e == b.e) {
		r.b = a.b + b.b;
		r.e = a.e;
	}
	if (a.e - b.e == 1) {
		r.b = a.b + b.b/10;
		r.e = a.e;
	}
	if (a.e - b.e == 2) {
		r.b = a.b + b.b/100;
		r.e = a.e;
	}
	if (a.e - b.e == 3) {
		r.b = a.b + b.b/1000;
		r.e = a.e;
	}
	if (b.e - a.e == 1) {
		r.b = a.b/10 + b.b;
		r.e = b.e;
	}
	if (b.e - a.e == 2) {
		r.b = a.b/100 + b.b;
		r.e = b.e;
	}
	if (b.e - a.e == 3) {
		r.b = a.b/1000 + b.b;
		r.e = b.e;
	}
	if ((r.b > 0 && r.b < 10) || (r.b < 0 && r.b > -10)) {
		r.b = r.b * 1000;
		r.e = r.e - 3;
	} else if ((r.b > 0 && r.b < 100) || (r.b < 0 && r.b > -100)) {
		r.b = r.b * 100;
		r.e = r.e - 2;
	} else if ((r.b > 0 && r.b < 1000) || (r.b < 0 && r.b > -1000)) {
	r.b = r.b * 10;
		r.e = r.e - 1;
	}
	return r;
}

double multiply(double a, double b) { // a * b
	double r;
	r.b = a.b * b.b;
	if (r.b % 1000 < 500) {
		r.b = r.b / 1000;
	} else {
		r.b = 1 + r.b / 1000;
	}
	r.e = a.e + b.e + 3;
	if ((r.b > 0 && r.b < 10) || (r.b < 0 && r.b > -10)) {
		r.b = r.b * 1000;
		r.e = r.e - 3;
	} else if ((r.b > 0 && r.b < 100) || (r.b < 0 && r.b > -100)) {
		r.b = r.b * 100;
		r.e = r.e - 2;
	} else if ((r.b > 0 && r.b < 1000) || (r.b < 0 && r.b > -1000)) {
		r.b = r.b * 10;
		r.e = r.e - 1;
	} else if (r.b > 9999 || r.b < -9999) {
		r.b = r.b / 10;
		r.e = r.e + 1;
	}
	return r;
}

double inverse(double a) { // 1 / a
	double r;
	if (a.b == 0) {
		return INFINITE_TIME_DOUBLE;
	}
	r.b = 1000000 / a.b;
	r.e = -6 - a.e;
	if ((r.b > 0 && r.b < 10) || (r.b < 0 && r.b > -10)) {
		r.b = r.b * 1000;
		r.e = r.e - 3;
	} else if ((r.b > 0 && r.b < 100) || (r.b < 0 && r.b > -100)) {
		r.b = r.b * 100;
		r.e = r.e - 2;
	} else if ((r.b > 0 && r.b < 1000) || (r.b < 0 && r.b > -1000)) {
		r.b = r.b * 10;
		r.e = r.e - 1;
	}
	return r;
}

time_t power(int a, int b) { // a ^ b (b >= 0)
	time_t r = 1;
	while (b > 0) {
		r = r * a;
		b = b - 1;
	}
	return r;
}

time_t round(double a) { // double --> integer
	if (a.e < -3) {
		if (a.b < 5000) return 0;
		else return 1;
	}
	if (a.e == -1) {
		if (a.b % 10 < 5) {
			return a.b / 10;
		} else {
			return 1 + a.b / 10;
		}
	}
	if (a.e == -2) {
		if (a.b % 100 < 50) {
			return a.b / 100;
		} else {
			return 1 + a.b / 100;
		}
	}
	if (a.e == -3) {
		if (a.b % 1000 < 500) {
			return a.b / 1000;
		} else {
			return 1 + a.b / 1000;
		}
	}
	return a.b * power(10, a.e);
}

double scenario1(double k, double r1, double r1Levels, bool r1Active) {
	double E;
	if (r1Active) { //If we depend on active R1, the level of activity is the value of E
		E = r1;
	} else { //otherwise we find the inactivity level via the total number of levels
		E = subtract(r1Levels, r1);
	}
	return multiply(k, E);
}


double scenario2_3(double k, double r2, double r2Levels, bool r2Active, double r1, double r1Levels, bool r1Active) {
	double E, S;
	if (r1Active) { //If we depend on active R1, the level of activity is the value of E
		E = r1;
	} else { //otherwise we find the inactivity level via the total number of levels
		E = subtract(r1Levels, r1);
	}
	if (r2Active) { //Same for R2
		S = r2;
	} else {
		S = subtract(r2Levels, r2);
	}
	return multiply(k, multiply(E, S));
}


double int_to_double(int a) { //Used to translate an activity level into double.
	double r;
	if (a < 10) {
		r.b = a * 1000;
		r.e = -3;
	} else if (a < 100) {
		r.b = a * 100;
		r.e = -2;
	} else if (a < 1000) {
		r.b = a * 10;
		r.e = -1;
	} else if (a < 10000) { //Our model supports up to 100 levels, so this should be the most it makes sense to check
		r.b = a;
		r.e = 0;
	}
	return r;
}

template.declaration:
int[-1, 1] delta, deltaNew, deltaOld, deltaOldOld, deltaOldOldOld;
bool deltaAlternating;
time_t tL, tU;
clock c;
double totalRate;


void updateDeltaOld() {
	deltaOldOldOld = deltaOldOld;
	deltaOldOld = deltaOld;
	deltaOld = deltaNew;
	deltaNew = delta;
	deltaAlternating = false;
	if (deltaOldOldOld != 0) { //We have updated delta at least 4 times, so we can see whether we have an oscillation
		if (deltaNew == deltaOldOld && deltaOld == deltaOldOldOld && deltaNew != deltaOld) { //Pairwise equal and alternating (e.g. +1, -1, +1, -1): we are oscillating
			deltaAlternating = true;
		}
	}
}

void update() {
	double R11_R8_r = scenario1(k_R11_R8, int_to_double(R11), int_to_double(R11Levels), true);
	totalRate = R11_R8_r;
	R11_R8.T = round(inverse(R11_R8_r));
	if (totalRate.b < 0) {
		delta = -1;
		totalRate.b = -totalRate.b;
	} else {
		delta = 1;
	}
	if (totalRate.b != 0) {
		tL = round(inverse(totalRate));
		tU = tL;
	} else {
		tL = INFINITE_TIME;
		tU = INFINITE_TIME;
	}
	if (tL != INFINITE_TIME && tL > tU) { //We use rounded things: maybe the difference between tL and tU was not so great, and with some rounding problems we could have this case
		tL = tU;
	}
	updateDeltaOld();
}

void react() {
	if (0 <= R + delta && R + delta <= MAX) {
		R = R + delta;
	}
	update();
}

bool can_react() {
	return !deltaAlternating && (tL != INFINITE_TIME && tL != 0 && tU != 0 && ((delta > 0 && R < MAX) || (delta < 0 && R > 0)));
}

bool cant_react() {
	return deltaAlternating || (tL == INFINITE_TIME || tL == 0 || tU == 0 || (delta > 0 && R == MAX) || (delta < 0 && R == 0));
}

