void update() {
	double R5_R6_r = scenario2_3(k_R5_R6, int_to_double(R6), int_to_double(R6Levels), true, int_to_double(R5), int_to_double(R5Levels), true);
	totalRate = subtract(zero, R5_R6_r);
	R5_R6.T = round(inverse(R5_R6_r));
	if (totalRate.b < 0) {
		delta = -1;
		totalRate.b = -totalRate.b;
	} else {
		delta = 1;
	}
	if (totalRate.b != 0) {
		tL = round(inverse(totalRate));
		tU = tL;
	} else {
		tL = INFINITE_TIME;
		tU = INFINITE_TIME;
	}
	if (tL != INFINITE_TIME && tL > tU) { //We use rounded things: maybe the difference between tL and tU was not so great, and with some rounding problems we could have this case
		tL = tU;
	}
	updateDeltaOld();
}

