	double R3_R4_r = scenario2_3(k_R3_R4, int_to_double(R4), int_to_double(R4Levels), false, int_to_double(R3), int_to_double(R3Levels), true);
	totalRate = add(subtract(zero, R7_R4_r), R3_R4_r);
