void update() {
	double R6_R2_r = scenario2_3(k_R6_R2, int_to_double(R2), int_to_double(R2Levels), true, int_to_double(R6), int_to_double(R6Levels), true);
	double R1_R2_r = scenario2_3(k_R1_R2, int_to_double(R2), int_to_double(R2Levels), false, int_to_double(R1), int_to_double(R1Levels), true);
	double R4_R2_r = scenario2_3(k_R4_R2, int_to_double(R2), int_to_double(R2Levels), false, int_to_double(R4), int_to_double(R4Levels), true);
	double R0_R2_r = scenario2_3(k_R0_R2, int_to_double(R2), int_to_double(R2Levels), false, int_to_double(R0), int_to_double(R0Levels), true);
	double R10_R2_r = scenario2_3(k_R10_R2, int_to_double(R2), int_to_double(R2Levels), true, int_to_double(R10), int_to_double(R10Levels), true);
	totalRate = subtract(add(add(add(subtract(zero, R6_R2_r), R1_R2_r), R4_R2_r), R0_R2_r), R10_R2_r);
	R6_R2.T = round(inverse(R6_R2_r));
	R1_R2.T = round(inverse(R1_R2_r));
	R4_R2.T = round(inverse(R4_R2_r));
	R0_R2.T = round(inverse(R0_R2_r));
	R10_R2.T = round(inverse(R10_R2_r));
	if (totalRate.b < 0) {
		delta = -1;
		totalRate.b = -totalRate.b;
	} else {
		delta = 1;
	}
	if (totalRate.b != 0) {
		tL = round(inverse(totalRate));
		tU = tL;
	} else {
		tL = INFINITE_TIME;
		tU = INFINITE_TIME;
	}
	if (tL != INFINITE_TIME && tL > tU) { //We use rounded things: maybe the difference between tL and tU was not so great, and with some rounding problems we could have this case
		tL = tU;
	}
	updateDeltaOld();
}

