	deltaNew = delta;
	deltaAlternating = false;
	if (deltaOldOldOld != 0) { //We have updated delta at least 4 times, so we can see whether we have an oscillation
		if (deltaNew == deltaOldOld && deltaOld == deltaOldOldOld && deltaNew != deltaOld) { //Pairwise equal and alternating (e.g. +1, -1, +1, -1): we are oscillating
			deltaAlternating = true;
		}
	}
}

void update() {
	double R4_R3_r = scenario2_3(k_R4_R3, int_to_double(R3), int_to_double(R3Levels), true, int_to_double(R4), int_to_double(R4Levels), true);
	double R2_R3_r = scenario2_3(k_R2_R3, int_to_double(R3), int_to_double(R3Levels), false, int_to_double(R2), int_to_double(R2Levels), true);
	double R9_R3_r = scenario2_3(k_R9_R3, int_to_double(R3), int_to_double(R3Levels), true, int_to_double(R9), int_to_double(R9Levels), true);
	totalRate = subtract(add(subtract(zero, R4_R3_r), R2_R3_r), R9_R3_r);
	R4_R3.T = round(inverse(R4_R3_r));
	R2_R3.T = round(inverse(R2_R3_r));
	R9_R3.T = round(inverse(R9_R3_r));
	if (totalRate.b < 0) {
		delta = -1;
		totalRate.b = -totalRate.b;
	} else {
		delta = 1;
	}
	if (totalRate.b != 0) {
		tL = round(inverse(totalRate));
		tU = tL;
	} else {
		tL = INFINITE_TIME;
		tU = INFINITE_TIME;
	}
	if (tL != INFINITE_TIME && tL > tU) { //We use rounded things: maybe the difference between tL and tU was not so great, and with some rounding problems we could have this case
		tL = tU;
	}
	updateDeltaOld();
}

void react() {
	if (0 <= R + delta && R + delta <= MAX) {
		R = R + delta;
	}
	update();
}

bool can_react() {
	return !deltaAlternating && (tL != INFINITE_TIME && tL != 0 && tU != 0 && ((delta > 0 && R < MAX) || (delta < 0 && R > 0)));
}

bool cant_react() {
	return deltaAlternating || (tL == INFINITE_TIME || tL == 0 || tU == 0 || (delta > 0 && R == MAX) || (delta < 0 && R == 0));
}

